import React from 'react';
import { BsChevronUp, BsChevronDown } from 'react-icons/bs';

const NewEntry = (props) => {
    const { collapsed, setCollapsed, classes } = props;

    const toggleCollapse = () => {
        setCollapsed(!collapsed);
    }

    return collapsed ? <BsChevronUp onClick={toggleCollapse} className={classes ? [ ...classes, 'collapse-btn' ] : 'collapse-btn'} /> : <BsChevronDown onClick={toggleCollapse} className={classes ? [ ...classes, 'collapse-btn' ] : 'collapse-btn'} />

}

export default NewEntry;
