import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, OverlayTrigger, Tooltip, Dropdown } from 'react-bootstrap';
import * as Diary from '../Diary';
import { BsPencil, BsChevronUp, BsChevronDown, BsClock, BsChevronBarExpand } from 'react-icons/bs';
import { FaPaperPlane } from 'react-icons/fa';
import { VscSmiley, VscFilter } from 'react-icons/vsc';
import { HiOutlineDotsHorizontal } from 'react-icons/hi';

const toolSize = 24;

const ToolBar = (props) => {
    const { setCollapseAllTrigger } = props;

    const tools = [
        {
            icon: <BsChevronUp size={toolSize} />,
            action: () => setCollapseAllTrigger(old => !old),
            label: 'Collapse All'
        },
        {
            // icon: <VscFilter size={toolSize} />,
            icon: (
                <Dropdown className="border border-primary">
                    <Dropdown.Toggle variant="clear p-0 m-0 border border-danger">
                        <VscFilter size={toolSize} />
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item>Action</Dropdown.Item>
                        <Dropdown.Item>Another action</Dropdown.Item>
                        <Dropdown.Item>Something else</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
            ),
            action: () => setCollapseAllTrigger(old => !old),
            label: 'Filter Entries'
        },
    ]

    return (
        <Row className="px-3 justify-content-between">
            {tools.map((tool, index) => {
                return (
                    <Col key={`tool-${index}`} >
                        <OverlayTrigger
                            placement='top'
                            overlay={
                                <Tooltip id={`tool-tooltip-${index}`}>
                                    {tool.label}
                                </Tooltip>
                            }
                        >
                            <div className="card py-2 card-btn" onClick={tool.action}>
                                {tool.icon}
                            </div>
                        </OverlayTrigger>
                    </Col>
                )
            })}
        </Row>
    )
}

export default ToolBar;
